// SPDX-License-Identifier: AGPL-1.0
pragma solidity 0.8.17;

import "./ERC20Internal.sol";
import "../Interfaces/IERC2612Standalone.sol";

abstract contract WithPermit is ERC20Internal, IERC2612Standalone {
	bytes32 internal constant PERMIT_TYPEHASH =
		keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
	mapping(address => uint256) internal _nonces;

	function DOMAIN_SEPARATOR() public view virtual override returns (bytes32);

	function nonces(address owner) external view override returns (uint256) {
		return _nonces[owner];
	}

	function permit(
		address owner,
		address spender,
		uint256 value,
		uint256 deadline,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external override {
		require(owner != address(0), "INVALID_ZERO_ADDRESS");

		uint256 currentNonce = _nonces[owner];
		bytes32 digest = keccak256(
			abi.encodePacked(
				"\x19\x01",
				DOMAIN_SEPARATOR(),
				keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, currentNonce, deadline))
			)
		);
		require(owner == ecrecover(digest, v, r, s), "INVALID_SIGNATURE");
		require(deadline == 0 || block.timestamp <= deadline, "TOO_LATE");

		_nonces[owner] = currentNonce + 1;
		_approveFor(owner, spender, value);
	}
}

src/ERC20/WithPermitAndFixedDomain.sol.content:
// SPDX-License-Identifier: AGPL-1.0
pragma solidity 0.8.17;

import "./ERC20Internal.sol";
import "../Interfaces/IERC2612Standalone.sol";

abstract contract WithPermitAndFixedDomain is ERC20Internal, IERC2612Standalone {
	bytes32 internal constant PERMIT_TYPEHASH =
		keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");

	// solhint-disable-next-line var-name-mixedcase
	bytes32 public immutable override DOMAIN_SEPARATOR;

	mapping(address => uint256) internal _nonces;

	constructor(string memory version) {
		if (bytes(version).length == 0) {
			version = "1";
		}
		DOMAIN_SEPARATOR = keccak256(
			abi.encode(
				keccak256("EIP712Domain(string name,string version,address verifyingContract)"),
				keccak256(bytes(name())),
				keccak256(bytes(version)),
				address(this)
			)
		);
	}

	function nonces(address owner) external view override returns (uint256) {
		return _nonces[owner];
	}

	function permit(
		address owner,
		address spender,
		uint256 value,
		uint256 deadline,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external override {
		require(owner != address(0), "INVALID_ZERO_ADDRESS");

		uint256 currentNonce = _nonces[owner];
		bytes32 digest = keccak256(
			abi.encodePacked(
				"\x19\x01",
				DOMAIN_SEPARATOR,
				keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, currentNonce, deadline))
			)
		);
		require(owner == ecrecover(digest, v, r, s), "INVALID_SIGNATURE");
		require(deadline == 0 || block.timestamp <= deadline, "TOO_LATE");

		_nonces[owner] = currentNonce + 1;
		_approveFor(owner, spender, value);
	}
}

src/GreetingsRegistry/GreetingsRegistry..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import "hardhat/console.sol";

contract GreetingsRegistry is Proxied {
	event MessageChanged(address indexed user, string message);

	mapping(address => string) public messages;
	string internal _prefix;

	function postUpgrade(string memory prefix) public proxied {
		_prefix = prefix;
	}

	constructor(string memory prefix) {
		// the proxied modifier from `hardhat-deploy` ensure postUpgrade effect can only be used once when the contract is deployed without proxy
		// by calling that function in the constructor we ensure the contract behave the same whether it is deployed through a proxy or not.
		postUpgrade(prefix);
	}

	function setMessage(string calldata message) external {
		string memory actualMessage = string(abi.encodePacked(_prefix, message));
		messages[msg.sender] = actualMessage;
		emit MessageChanged(msg.sender, actualMessage);
	}
}

